mapper.select:
SELECT idEspecie, descripcionEspecie, observacion FROM tb_Especie WHERE estado=1
AND idEspecie=#{idESPE}
AND descripcionEspecie like '%${DESC}%'

mapper.update:
UPDATE tb_especie 
SET estado=0
WHERE idEspecie=#{idESPE}

UPDATE tb_especie 
SET observacion=#{observacion}
WHERE idEspecie=#{idESPE}
