mapper.select:
SELECT r.idRaza, r.descripcionRaza, r.observacion, e.descripcionEspecie FROM tb_raza r INNER JOIN tb_especie e ON r.idEspecie=e.idEspecie WHERE r.estado=1
AND idRaza=#{idRAZA}
AND descripcionRaza like '%${DESC}%'

mapper.update:
UPDATE tb_raza 
SET estado=0
WHERE idRaza=#{idRAZA}

UPDATE tb_raza 
SET observacion=#{observacion}
WHERE idRaza=#{idRAZA}
