mapper.select:
SELECT r.idRaza, r.descripcionRaza, r.observacion, e.descripcionEspecie, r.actualizado, r.registrado FROM tb_raza r INNER JOIN tb_especie e ON r.idEspecie=e.idEspecie WHERE r.estado=1
AND r.idRaza=#{idRAZA}
AND r.descripcionRaza like '%${DESC}%'

mapper.update:
UPDATE tb_raza 
SET estado=0, actualizado=SYSDATE()
WHERE idRaza=#{idRAZA}

UPDATE tb_raza 
SET observacion=#{observacion}, idEspecie=#{idEspecie}, actualizado=SYSDATE()
WHERE idRaza=#{idRAZA}
