mapper.select:
SELECT distinct r.idReceta, r.idAM, r.observacion, r.registrado FROM tb_receta r inner join tb_atencion_medica am WHERE r.estado=1 and am.estadoAM=1
AND r.idReceta=#{idReceta}
AND r.idAM=#{idAM}

mapper.update:
UPDATE tb_receta 
SET estado=0, actualizado=SYSDATE()
WHERE idReceta=#{idReceta}

UPDATE tb_receta_detalle 
SET estado=0, actualizado=SYSDATE()
WHERE idReceta=#{idReceta}

UPDATE tb_receta 
SET observacion=#{observacion}
WHERE idReceta=#{idReceta}
